mapper.select:
select
id, name, pwd, alias, certificate_num, status, create_time, update_time
from electronic_commerce.tf_c_user


        and id = #{id}


        and name = #{name}


        and pwd = #{pwd}


        and alias = #{alias}


        and certificate_num = #{certificateNum}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}
